  MovieSubscription(id: "1368337", title: "Dune: Part Three", releaseDate: "2026-12-18", posterBase64: "", type: "movie"),
  MovieSubscription(id: "1368337", title: "Dune: Part Three", releaseDate: "2026-12-18", posterBase64: "", type: "movie"),
]

// MARK: - Widget Provider
struct Provider: TimelineProvider {
    func placeholder(in context: Context) -> SimpleEntry {
        SimpleEntry(date: Date(), subscriptions: [])
    }

    func getSnapshot(in context: Context, completion: @escaping (SimpleEntry) -> Void) {
        let entry = SimpleEntry(date: Date(), subscriptions: getSubscriptions())
        completion(entry)
    }

    func getTimeline(in context: Context, completion: @escaping (Timeline<SimpleEntry>) -> Void) {
        let currentDate = Date()
        let subscriptions = getSubscriptions()

        // Update the widget every hour
        let nextUpdate = Calendar.current.date(byAdding: .hour, value: 1, to: currentDate)!
        let entry = SimpleEntry(date: currentDate, subscriptions: subscriptions)

        let timeline = Timeline(entries: [entry], policy: .after(nextUpdate))
        completion(timeline)
    }

    // Fetch subscriptions from shared storage
    private func getSubscriptions() -> [MovieSubscription] {
        guard let defaults = UserDefaults(suiteName: "group.com.lookforward.app") else {
            print("Widget: Failed to get UserDefaults with suite name")
            return []
        }

        guard let jsonString = defaults.string(forKey: "subscriptions") else {
            print("Widget: No subscriptions data found in shared storage")
            return []
        }

        print("Widget: Raw JSON from storage: \(jsonString)")

        guard let jsonData = jsonString.data(using: .utf8) else {
            print("Widget: Failed to convert JSON string to data")
            return []
        }

        do {
            let subscriptions = try JSONDecoder().decode([MovieSubscription].self, from: jsonData)
            print("Widget: Successfully decoded \(subscriptions.count) subscriptions")
            for sub in subscriptions {
                print("  - \(sub.title) | posterBase64 length: \(sub.posterBase64.count)")
            }
            return subscriptions
        } catch {
            print("Widget: Error decoding subscriptions: \(error)")
            return []
        }
    }
}

struct SimpleEntry: TimelineEntry {
    let date: Date
    let subscriptions: [MovieSubscription]
}

// MARK: - Widget Views
struct widgetEntryView: View {
    var entry: Provider.Entry
    @Environment(\.widgetFamily) var widgetFamily

    var body: some View {
        switch widgetFamily {
        case .systemSmall:
            SmallWidgetView(entry: entry)
        case .systemMedium:
            MediumWidgetView(entry: entry)
        case .systemLarge:
            LargeWidgetView(entry: entry)
        default:
            SmallWidgetView(entry: entry)
        }
    }
}

// MARK: - Small Widget View
struct SmallWidgetView: View {
    var entry: Provider.Entry

    var body: some View {
        VStack(alignment: .leading, spacing: 0) {
            if entry.subscriptions.isEmpty {
                VStack(spacing: 4) {
                    Text("LookForward Pro")
                        .font(.system(size: 13, weight: .semibold))
                        .foregroundColor(.primary)
                    Text("Upgrade to see your countdowns here")
                        .font(.system(size: 10, weight: .regular))
                        .foregroundColor(.secondary)
                        .multilineTextAlignment(.center)
                }
                .frame(maxWidth: .infinity, maxHeight: .infinity)
            } else {
                let items = Array(entry.subscriptions.prefix(2))
                VStack(alignment: .leading, spacing: 10) {
                    ForEach(Array(items.enumerated()), id: \.offset) { index, subscription in
                        VStack(alignment: .leading, spacing: 4) {
                            Text(subscription.title)
                                .font(.system(size: 13, weight: .semibold))
                                .foregroundColor(.primary)
                                .lineLimit(2)

                            if !subscription.releaseDate.isEmpty {
                                Text(formatDate(subscription.releaseDate).uppercased())
                                    .font(.system(size: 10, weight: .medium))
                                    .foregroundColor(.secondary)
                            }
                        }

                        if index < items.count - 1 {
                            Divider()
                                .background(Color.gray.opacity(0.3))
                        }
                    }
                }
            }

        }
        .frame(maxWidth: .infinity, maxHeight: .infinity, alignment: .center)
        .padding(0)
        .containerBackground(.background, for: .widget)
    }
}

