import SwiftUI
import WidgetKit

enum SubscriptionType: String, Codable {
  case movie
  case game
}

// MARK: - Data Models
struct Subscription: Codable, Hashable {
  let id: String  // Movies can be int, need to check for games
  let title: String
  let releaseDate: String  // Movies come in as 2026-07-15
  let posterBase64: String
  let type: SubscriptionType
}

