// MARK: - Poster Image View
struct PosterImage: View {
  let posterBase64: String

  var body: some View {
    if let image = base64ToImage(posterBase64) {
      Image(uiImage: image)
        .resizable()
        .aspectRatio(contentMode: .fit)
        .clipped()
    } else {
      Color.gray.opacity(0.3)
        .overlay(
          Image(systemName: "photo")
            .foregroundColor(.gray)
        )
    }
  }

  private func base64ToImage(_ base64String: String) -> UIImage? {
    guard !base64String.isEmpty,
      let imageData = Data(base64Encoded: base64String)
    else {
      print("PosterImage: Failed to decode base64 string")
      return nil
    }

    guard let image = UIImage(data: imageData) else {
      print("PosterImage: Failed to create UIImage from data")
      return nil
    }

    print("PosterImage: Successfully loaded image from base64")
    return image
  }
}

// MARK: - Helper Functions
func formatDate(_ dateString: String) -> String {
  let formatter = ISO8601DateFormatter()
  formatter.formatOptions = [.withFullDate]

  guard let date = formatter.date(from: dateString) else {
    return dateString
  }

  let displayFormatter = DateFormatter()
  displayFormatter.dateStyle = .medium
  displayFormatter.timeStyle = .none

  return displayFormatter.string(from: date)
}

func daysUntilRelease(_ dateString: String) -> Int? {
  let formatter = ISO8601DateFormatter()
  formatter.formatOptions = [.withFullDate]

  guard let releaseDate = formatter.date(from: dateString) else {
    return nil
  }

  let calendar = Calendar.current
  let now = calendar.startOfDay(for: Date())
  let release = calendar.startOfDay(for: releaseDate)

  let components = calendar.dateComponents([.day], from: now, to: release)
  return components.day
}

// MARK: - Widget Configuration
struct widget: Widget {
  let kind: String = "widget"

  var body: some WidgetConfiguration {
    StaticConfiguration(kind: kind, provider: Provider()) { entry in
      widgetEntryView(entry: entry)
    }
    .configurationDisplayName("LookForward Countdowns")
    .description("View your movie and game countdowns")
    .supportedFamilies([.systemSmall, .systemMedium, .systemLarge])
  }
}

